func InitializeTiles(space *cp.Space, gameboard [][]int) {
	width := GameBoardTileWidth
	height := GameBoardTileHeight
	for ty := 0; ty < height; ty++ {
		for tx := 0; tx < width; tx++ {
			tile := gameboard[ty][tx]
			if tile > 0 {
				x, y := float64(tx*16), float64(ty*16)
				tileBody := cp.NewStaticBody()
				tileBody.SetPosition(cp.Vector{X: x, Y: y})
				var tileShape *cp.Shape
				// make "center" blocks slightly wider, and edge blocks slightly narrower
				if tile == 2 || tile == 4 {
					tileShape = cp.NewBox(tileBody, 24, 16, 0)
				} else {
					tileShape = cp.NewBox(tileBody, 14, 16, 0)
				}
				tileShape.SetElasticity(0)
				tileShape.SetFriction(1)
				tileShape.SetCollisionType(entity.BlockCollisionType)
				space.AddBody(tileBody)
				space.AddShape(tileShape)
			}
		}
	}
}
